#!/bin/bash

#------------------------------- create log file ------------------------------------------#
log_file=${PBS_O_INITDIR}/first_node.log
touch ${log_file}

#------------------------------- print enviroment variables -------------------------------#
echo "$(date +"%Y/%m/%d %H:%M:%S")][INFO] PBS_JOBID=${PBS_JOBID}" | tee -a ${log_file}
echo "$(date +"%Y/%m/%d %H:%M:%S")][INFO] PBS_O_INITDIR=${PBS_O_INITDIR}" | tee -a ${log_file}
echo "$(date +"%Y/%m/%d %H:%M:%S")][INFO] PBS_NODEFILE=${PBS_NODEFILE}" | tee -a ${log_file}
echo "$(date +"%Y/%m/%d %H:%M:%S")][INFO] PBS_NP=${PBS_NP}" | tee -a ${log_file}
echo "$(date +"%Y/%m/%d %H:%M:%S")][INFO] hadoop_bin_dir: ${hadoop_bin_dir}" | tee -a ${log_file}
echo "$(date +"%Y/%m/%d %H:%M:%S")][INFO] hdfs_job_package: ${hdfs_job_package}" | tee -a ${log_file}
echo "$(date +"%Y/%m/%d %H:%M:%S")][INFO] mpi_nodes: "`cat ${PBS_NODEFILE}` | tee -a ${log_file}

#------------------------------- download package -----------------------------------------#
package_name=`basename ${hdfs_job_package}`
echo "$(date +"%Y/%m/%d %H:%M:%S")][INFO] package_name = ${package_name}" | tee -a ${log_file}

/usr/local/bin/mpirun rm -f ${PBS_O_INITDIR}/${package_name}
/usr/local/bin/mpirun ${hadoop_bin_dir}/hadoop fs -get ${hdfs_job_package} ${PBS_O_INITDIR}/${package_name}
/usr/local/bin/mpirun tar -xf ${PBS_O_INITDIR}/${package_name} -C ${PBS_O_INITDIR}

#------------------------------- initialize package ---------------------------------------#
package_root_dir="${PBS_O_INITDIR}/"`ls -l | grep "^d" | awk '{ print $NF }'`
echo "$(date +"%Y/%m/%d %H:%M:%S")][INFO] package_root_dir = ${package_root_dir}" | tee -a ${log_file}

cd ${package_root_dir}
echo "$(date +"%Y/%m/%d %H:%M:%S")][INFO] pwd = $(pwd)" >> ${log_file}

/usr/local/bin/mpirun mkdir -p log
/usr/local/bin/mpirun mkdir -p output
/usr/local/bin/mpirun chmod 777 bin/param-server
/usr/local/bin/mpirun chmod 777 bin/rtsparse-learner

${hadoop_bin_dir}/hadoop fs -rm ${hdfs_job_package}
#------------------------------- run package ----------------------------------------------#
echo "$(date +"%Y/%m/%d %H:%M:%S")][INFO] training ..." >> ${log_file}

